import React, { useState, useEffect } from 'react';

function Data() {
  const [breeds, setBreeds] = useState([]);
  const [selectedBreed, setSelectedBreed] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch dog breeds from the API and update the state
    fetch('https://dog.ceo/api/breeds/list/all')
      .then((response) => response.json())
      .then((data) => {
        setBreeds(data.message); // Use data.message for the list of breeds
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  }, []);

  const handleBreedClick = (breedName) => {
    setSelectedBreed(breeds.find((breed) => breed === breedName));
  };

  return (
    <div>
      <h1>Dog Breeds:</h1>
      {loading ? (
        <p>Loading...</p>
      ) : breeds.length > 0 ? ( // Check if breeds is not empty
        <div>
          <ul>
            {breeds.map((breed) => (
              <li
                key={breed}
                onClick={() => handleBreedClick(breed)}
              >
                {breed}
              </li>
            ))}
          </ul>
          {selectedBreed && (
            <div>
              <h2>Selected Breed: {selectedBreed}</h2>
              {/* Display breed details here */}
            </div>
          )}
        </div>
      ) : (
        <p>No dog breeds found.</p>
      )}
    </div>
  );
}

export default Data;
